var rating=function(elementClass){var ele=document.getElementsByClassName(elementClass);var rating=0,maxRating=0;if(ele.length!==0){rating=ele[0].getAttribute("data-rating");maxRating=ele[0].getAttribute("data-max-rating")}for(var i=0;i<maxRating;i++){var icon=document.createElement("i");icon.className="icon";icon.setAttribute("poxs",i);if(i<rating){icon.className="icon active"}if(!$("."+elementClass).hasClass("disabled")){addRatingListener(icon,elementClass)}ele[0].appendChild(icon)}};function addRatingListener(element,elementClass){element.addEventListener("mouseenter",function(event){var starEnd=event.target.getAttribute("poxs");var icons=$("."+elementClass+">i");for(var item=0;item<=starEnd;item++){if(!icons.eq(item).hasClass("selected")){icons.eq(item).addClass("selected")}}});element.addEventListener("mouseleave",function(event){var starEnd=event.target.getAttribute("poxs");var icons=$("."+elementClass+">i");for(var item=starEnd;item<icons.length;item++){if(icons.eq(item).hasClass("selected")){icons.eq(item).removeClass("selected")}}icons.removeClass("selected")});element.addEventListener("click",function(event){var starEnd=event.target.getAttribute("poxs");var icons=$("."+elementClass+">i");for(var item=0;item<=starEnd;item++){if(!icons.eq(item).hasClass("active")){icons.eq(item).addClass("active")}}for(var item1=parseInt(starEnd)+1;item1<icons.length;item1++){icons.eq(item1).removeClass("active")}})};